mapper.select:
SELECT a.achievement_id, a.name, a.content, a.photo, a.acquisition_condition, a.score, a.max_count, COALESCE(SUM(CASE WHEN ua.progress = 100 THEN 1 ELSE 0 END), 0) AS complete_count FROM achievement a LEFT JOIN user_achievement ua ON a.achievement_id = ua.achievement_id WHERE 1 = 1
AND a.achievement_id LIKE CONCAT('%', #{achievement.id}, '%')
AND a.name LIKE CONCAT('%', #{achievement.name}, '%')
AND a.content LIKE CONCAT('%', #{achievement.content}, '%')
AND a.score = #{achievement.score}
GROUP BY a.achievement_id, a.name, a.content, a.photo, a.acquisition_condition, a.location
HAVING COALESCE(SUM(CASE WHEN ua.progress = 100 THEN 1 ELSE 0 END), 0) = #{achievement.completeCount}
ORDER BY a.achievement_id DESC LIMIT #{pageNo}, #{pageCount};

mapper.update:

update user_achievement set
    is_primary = 1
where
    user_id=#{user.no}
    and achievement_id=#{id}

update user_achievement set
    current_count = #{currentCount},
    progress = #{progress}
where
    user_id = #{user.no}
    and achievement_id = #{id}

mapper.select:
SELECT count(*) FROM (SELECT count(*) FROM achievement a LEFT JOIN user_achievement ua ON a.achievement_id = ua.achievement_id WHERE 1 = 1
AND a.achievement_id LIKE CONCAT('%', #{id}, '%')
AND a.name LIKE CONCAT('%', #{name}, '%')
AND a.content LIKE CONCAT('%', #{content}, '%')
AND a.score = #{score}
GROUP BY a.achievement_id, a.name, a.content, a.photo, a.acquisition_condition, a.location
HAVING COALESCE(SUM(CASE WHEN ua.progress = 100 THEN 1 ELSE 0 END), 0) = #{completeCount}
) AS count_table

mapper.update:
update user_achievement set
    progress = 100,
    get_date = now()
where
    user_id = #{user.no}
    and achievement_id = #{id}
