mapper.update:
update alert
set alert_date = now()
where alert_id = #{no}

update alert
set alert_read = 1
where alert_id = #{no}

update alert
set alert_read = 1
where alert_category = #{category}
  and alert_category_id = #{categoryNo}
  and user_id = #{user.no}

mapper.select:
select
  count(*)
from
  alert
where
  alert_category = #{category}
  and alert_category_id = #{categoryNo}
  and user_id = #{user.no}
  and alert_read = 0
