mapper.insert:
INSERT INTO collection_board (
board_id,
collection_id
)
VALUES (
#{no},
#{collection.no}
)

mapper.update:
UPDATE trade_board
SET
collection_id = #{collection.no},
price = #{price}
WHERE
board_id = #{no}

UPDATE board
SET is_deleted = 1
WHERE board_id = #{no}

UPDATE board
SET view_count = view_count + 1
WHERE board_id = #{no}

UPDATE board
SET is_popular = #{isPopular}
WHERE board_id = #{no}

mapper.select:
SELECT b.board_id, b.title, b.user_id, u.user_id as writer_id, u.nickname, u.end_date, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type, COUNT(l.board_id) AS like_count FROM board b left join user u on b.user_id = u.user_id left join likes l on b.board_id = l.board_id WHERE b.is_deleted = 0
AND b.board_id = #{board.no}
AND b.title LIKE CONCAT('%', #{board.title}, '%')
AND u.nickname LIKE CONCAT('%', #{board.user.nickname}, '%')
AND b.post_date LIKE CONCAT(#{board.postDateString}, '%')
AND b.view_count = #{board.viewCount}
GROUP BY b.board_id, b.title, b.user_id, u.user_id, u.nickname, u.end_date, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type
HAVING COUNT(l.board_id) = #{board.likeCount}
ORDER BY b.board_id desc LIMIT #{pageNo}, #{pageCount}

SELECT count(*) FROM (SELECT count(*) FROM board b left join user u on b.user_id = u.user_id left join likes l on b.board_id = l.board_id WHERE b.is_deleted = 0
AND b.board_id = #{no}
AND b.title LIKE CONCAT('%', #{title}, '%')
AND u.nickname LIKE CONCAT('%', #{user.nickname}, '%')
AND b.post_date LIKE CONCAT(#{postDateString}, '%')
AND b.view_count = #{viewCount}
GROUP BY b.board_id, b.title, b.user_id, u.user_id, u.nickname, u.end_date, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type
HAVING COUNT(l.board_id) = #{likeCount}
) AS count_table

SELECT b.board_id, b.title, b.content, b.user_id, u.nickname, u.end_date, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type, bp.photo_id, bp.filename, bp.origin_filename FROM board b LEFT JOIN user u ON b.user_id = u.user_id LEFT JOIN board_photo bp ON b.board_id = bp.board_id AND bp.photo_id = ( SELECT MIN(photo_id) FROM board_photo WHERE board_id = b.board_id ) WHERE b.is_deleted = 0 AND b.board_type = 'general'
AND b.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY b.board_id DESC LIMIT #{offset}, #{limit}

SELECT b.board_id, b.title, b.content, b.user_id, u.nickname, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type, mc.maincategory_id, mc.category_name, bp.photo_id, bp.filename, bp.origin_filename FROM board b LEFT JOIN maincategory mc ON b.maincategory_id = mc.maincategory_id LEFT JOIN user u ON b.user_id = u.user_id LEFT JOIN board_photo bp ON b.board_id = bp.board_id WHERE b.is_deleted = 0 AND b.board_type = 'general'
AND b.title LIKE CONCAT('%', #{keyword}, '%')
AND b.maincategory_id = #{categoryNo}
ORDER BY b.board_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM board b LEFT JOIN maincategory mc ON b.maincategory_id = mc.maincategory_id WHERE b.is_deleted = 0 AND b.board_type = 'general'
AND b.title LIKE CONCAT('%', #{keyword}, '%')
AND b.maincategory_id = #{categoryNo}

SELECT b.board_id, b.title, b.content, b.user_id, u.nickname, b.post_date, b.view_count, b.is_public, b.is_deleted, b.board_type, mc.maincategory_id, mc.category_name, bp.photo_id, bp.filename, bp.origin_filename FROM board b LEFT JOIN maincategory mc ON b.maincategory_id = mc.maincategory_id LEFT JOIN user u ON b.user_id = u.user_id LEFT JOIN board_photo bp ON b.board_id = bp.board_id WHERE b.is_deleted = 0 AND b.is_popular = TRUE
AND b.title LIKE CONCAT('%', #{keyword}, '%')
AND b.maincategory_id = #{categoryNo}
ORDER BY b.board_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM board b LEFT JOIN maincategory mc ON b.maincategory_id = mc.maincategory_id WHERE b.is_deleted = 0 AND b.is_popular = TRUE
AND b.title LIKE CONCAT('%', #{keyword}, '%')
AND b.maincategory_id = #{categoryNo}
