mapper.select:
SELECT NULL AS subcategory_id, NULL AS category_name, m.maincategory_id, m.category_name AS maincategory_name, COALESCE(SUM(CASE WHEN c.subcategory_id IS NOT NULL THEN 1 ELSE 0 END), 0) AS count FROM maincategory m LEFT JOIN subcategory s ON m.maincategory_id = s.maincategory_id LEFT JOIN collection c ON s.subcategory_id = c.subcategory_id GROUP BY m.maincategory_id, m.category_name HAVING m.maincategory_id = #{no}
UNION ALL
SELECT s.subcategory_id, s.category_name, s.maincategory_id, m.category_name AS maincategory_name, COALESCE(COUNT(c.subcategory_id), 0) AS count FROM subcategory s LEFT JOIN maincategory m ON s.maincategory_id = m.maincategory_id LEFT JOIN collection c ON s.subcategory_id = c.subcategory_id GROUP BY s.subcategory_id, s.category_name, s.maincategory_id, m.category_name HAVING s.maincategory_id = #{no}
ORDER BY subcategory_id, maincategory_id

SELECT m.maincategory_id, m.category_name, COALESCE(SUM(CASE WHEN c.subcategory_id IS NOT NULL THEN 1 ELSE 0 END), 0) AS count FROM maincategory m LEFT JOIN subcategory s ON m.maincategory_id = s.maincategory_id LEFT JOIN collection c ON s.subcategory_id = c.subcategory_id WHERE 1 = 1
AND m.maincategory_id = #{maincategory.no}
AND m.category_name LIKE CONCAT('%', #{maincategory.name}, '%')
GROUP BY m.maincategory_id, m.category_name
HAVING COALESCE(SUM(CASE WHEN c.subcategory_id IS NOT NULL THEN 1 ELSE 0 END), 0) = #{maincategory.count}
ORDER BY maincategory_id LIMIT #{pageNo}, #{pageCount}

SELECT count(*) FROM (SELECT count(*) FROM maincategory m LEFT JOIN subcategory s ON m.maincategory_id = s.maincategory_id LEFT JOIN collection c ON s.subcategory_id = c.subcategory_id WHERE 1 = 1
AND m.maincategory_id = #{no}
AND m.category_name LIKE CONCAT('%', #{name}, '%')
GROUP BY m.maincategory_id, m.category_name
HAVING COALESCE(SUM(CASE WHEN c.subcategory_id IS NOT NULL THEN 1 ELSE 0 END), 0) = #{count}
) AS count_table
