mapper.update:
update user set
last_login = #{lastLoginTime}
WHERE user_id = #{userId}

mapper.select:
select u.user_id, u.nickname, u.email, u.admin, u.photo, u.start_date, u.end_date, u.last_login, us.sns_account_id, us.provider from user u left join user_sns us on u.user_id = us.user_id where 1 = 1
AND u.user_id = #{user.no}
AND u.email LIKE CONCAT('%', #{user.email}, '%')
AND u.nickname LIKE CONCAT('%', #{user.nickname}, '%')
AND u.start_date LIKE CONCAT(#{user.startDateString}, '%')
AND u.last_login LIKE CONCAT(#{user.lastLoginString}, '%')
AND u.admin = #{user.admin}
AND us.provider LIKE CONCAT('%', #{user.userSns.provider}, '%')
AND u.end_date IS NULL order by u.user_id desc limit #{pageNo}, #{pageCount}

mapper.update:
update user
set admin = #{no}
where user_id = #{userNo}
